SELECT (SELECT UPPER(STUFF((SELECT ','+ProjectID from dbo.B_PojectChannelOrgRel where OrgID=t.ID AND IsDel=0 AND Status=1 for XML PATH('')),1,1,''))) ProjectIDs,t.* FROM ( SELECT ROW_NUMBER() OVER ( ORDER BY a.CreateTime DESC ) ID1516, a.ID,a.OrgCode,a.OrgName,a.OrgShortName,a.BizLicense,a.[Status],a.CreateTime,a.ProjectID, case a.[Status] when 0 then '禁用' when 1 then '启用' else '' end as StatuName, sa.EmployeeName AS CreatorName, b.ID AS LeaderID,b.UserName,b.[Password],b.Name AS OrgLeaderName,STUFF(b.Mobile ,4,4,'****') AS OrgLeaderMobileSt, b.Mobile as OrgLeaderMobile, b.[Status] AS OrgLeaderStatu,case b.[Status] when 0 then '禁用' when 1 then '启用' else '' end as OrgLeaderStatuName , a.[Status] AS OrgStatu,case a.[Status] when 0 then '禁用' when 1 then '启用' else '' end as OrgStatuName ,a.ChannelTypeID,[dbo].[F_DictName](a.ChannelTypeID) as ChannelType FROM [dbo].[B_ChannelOrg] a LEFT JOIN dbo.B_ChannelUser b ON a.ID=b.ChannelOrgID AND b.IsDel=0 and b.job=0 left join S_Account sa on a.Creator=sa.id WHERE a.IsDel=0 and a.OrgCategory=1 and a.ID IN (SELECT DISTINCT OrgID FROM [dbo].[B_PojectChannelOrgRel] WHERE ProjectID=#{ProjectID} AND IsDel=0 AND Status=1)
${sqlWhere}
) t WHERE 1=1 and t.ID1516 > ( #{pageNum} - 1 ) * #{pageSize} AND t.ID1516 <= #{pageNum} * #{pageSize}

SELECT ID, OrgCode, OrgName, OrgShortName FROM [dbo].[B_ChannelOrg] WHERE IsDel = 0 AND [Status] = 1 AND ProjectID = #{ProjectID}
AND OrgName like '%#{pOrgName}%'
ORDER BY CreateTime DESC
