SELECT t.OpportunityID INTO #table FROM ( select * from ( SELECT * <!--最近到访--> FROM ( SELECT b.ID OpportunityID , d.CreateTime, b.Status OpportunityStatus, b.TheFirstVisitDate, 0 ClueStatus, G.CreateTime ZJDF, b.TheLatestFollowUpDate FROM B_Opportunity b LEFT JOIN B_Customer a ON a.ID = b.CustomerID LEFT JOIN B_Clue d ON b.ClueID = d.ID AND d.Status<>3 AND d.IntentProjectID =#{ProjectID} LEFT JOIN B_SalesGroupMember SM ON SM.MemberID=b.SaleUserID AND SM.IsDel=0 AND SM.ProjectID=#{ProjectID} AND sm.RoleID='0269F35E-B32D-4D12-8496-4E6E4CE597B7' LEFT JOIN B_ChannelOrg CO ON CO.ID=d.ReportUserOrg AND CO.ProjectID = #{ProjectID} LEFT JOIN( SELECT OpportunityID,MAX(CreateTime) CreateTime FROM dbo.B_CustomerFollowUp WHERE FollwUpType='69331990-DBF4-0A2F-80CD-7BC424AA8908' AND ProjectID=#{ProjectID} {Where} GROUP BY OpportunityID ) G ON G.OpportunityID=b.ID WHERE b.IsDel = 0 AND b.ProjectID = #{ProjectID} {OppWhere} UNION SELECT b.ID OpportunityID , b.CreateTime , 0 OpportunityStatus, '' TheFirstVisitDate, b.Status ClueStatus, '' ZJDF, '' TheLatestFollowUpDate FROM B_Clue b LEFT JOIN B_ChannelOrg CO ON CO.ID=b.ReportUserOrg LEFT JOIN dbo.B_CustomerPotential CP ON b.CustomerPotentialID=CP.ID WHERE b.IsDel = 0 AND b.IntentProjectID = #{ProjectID} AND b.SourceType IS NOT NULL AND b.SourceType<>'0390CD8C-D6D4-4C92-995B-08C7E18E6EC2' AND NOT EXISTS ( SELECT ID FROM B_Opportunity d WHERE d.IsDel = 0 AND d.ProjectID = #{ProjectID} AND d.ClueID = b.ID ) {ClueWhere} ) m where 1=1 {sqlWhere} ) s where 1=1 {WhereLast} ) t WHERE 1=1
SELECT t.OpportunityID , t.CustomerID ,<!--客户ID--> t.CustomerName ,<!--客户姓名--> STUFF(t.CustomerMobile ,4,4,'****') CustomerMobile , CASE dbo.F_DictName(t.CustomerRank) WHEN '' THEN '1级' WHEN NULL THEN '1级' ELSE dbo.F_DictName(t.CustomerRank) END CustomerRankName ,<!--客储等级名称--> dbo.F_GetClueStatusName(t.ClueStatus, t.OpportunityStatus) CustomerStatus , dbo.F_GetProjectName(t.ProjectID) AS IntentProjectName , dbo.F_DictName(t.OpportunitySource) OpportunitySource , dbo.F_GetMediaLargeName(t.CognitiveChannel) CognitiveChannel , dbo.F_GetMediaChildName(t.CognitiveChannelSub) CognitiveChannelSub , t.SaleUserID , t.SaleUserName , t.TheFirstVisitDate , <!--首访时间--> t.ZJDF,<!--最近到访--> t.TheLatestFollowUpDate , <!--最后跟进时间--> t.CreateTime ,<!--创建时间--> t.Gender ,<!--性别--> dbo.F_DictName(t.SourceType) AS SourceType ,<!--渠道类型--> t.ReportUserID , t.ReportUserName , t.ReportUserMobile , t.ChannelName , t.ReportTime , <!--报备时间 --> dbo.F_DictName(t.Gender) GenderTxt, dbo.F_GetSaleGroupName(t.SaleUserID, t.ProjectID) AS SaleTeamName, <!--置业顾问团队名称--> STUFF( ( SELECT ',' + r.RoomCode FROM dbo.B_Room r INNER JOIN dbo.B_Building b ON r.BuildingID=b.ID WHERE r.ID IN (SELECT mt.RoomGUID FROM dbo.B_Opportunity o INNER JOIN dbo.B_CustomerAttach ca ON o.ID=ca.OpportunityID INNER JOIN dbo.C_MYTrade mt ON mt.OppGUID=ca.ID WHERE o.ID=t.OpportunityID) AND r.ProjectID in (select ID from B_Project where PID=#{ProjectID}) FOR XML PATH('') ) , 1, 1, '') Room FROM ( SELECT *, m.SourceType SourceTypeID FROM ( SELECT b.ID OpportunityID , <!--b.ClueID ,--> b.CustomerID ,<!--客户ID--> b.CustomerName ,<!--客户姓名--> b.CustomerMobile ,<!--客户手机号--> b.CustomerRank ,<!--客储等级--> b.Status OpportunityStatus , b.ProjectID ,<!--意向项目ID--> b.OpportunitySource ,<!--认知途径ID--> b.CognitiveChannel , b.CognitiveChannelSub , b.SaleUserID , b.SaleUserName , b.TheFirstVisitDate , <!--首访时间--> b.TheLatestFollowUpDate , <!--最后跟进时间--> d.CreateTime ,<!--创建时间--> a.Gender ,<!--性别--> d.SourceType , d.ReportUserID , d.ReportUserName , d.ReportUserMobile , d.CreateTime AS ReportTime , <!--报备时间--> d.Status ClueStatus, SM.ReceptionGroupID SaleTeamID, CO.OrgName ChannelName, d.ReportUserOrg, G.CreateTime ZJDF FROM B_Opportunity b LEFT JOIN B_Customer a ON a.ID = b.CustomerID LEFT JOIN B_Clue d ON b.ClueID = d.ID AND d.Status<>3 AND d.IntentProjectID =#{ProjectID} LEFT JOIN B_SalesGroupMember SM ON SM.MemberID=b.SaleUserID AND SM.IsDel=0 AND SM.ProjectID=#{ProjectID} AND sm.RoleID='0269F35E-B32D-4D12-8496-4E6E4CE597B7' LEFT JOIN B_ChannelOrg CO ON CO.ID=d.ReportUserOrg <!--AND CO.ProjectID = #{ProjectID}--> LEFT JOIN( SELECT OpportunityID,MAX(CreateTime) CreateTime FROM dbo.B_CustomerFollowUp WHERE FollwUpType='69331990-DBF4-0A2F-80CD-7BC424AA8908' AND ProjectID=#{ProjectID} {Where} GROUP BY OpportunityID ) G ON G.OpportunityID=b.ID WHERE b.ProjectID=#{ProjectID} AND EXISTS(SELECT OpportunityID FROM #table tab WHERE tab.OpportunityID=b.ID) UNION SELECT b.ID OpportunityID , <!--b.ID ClueID ,--> b.CustomerPotentialID CustomerID ,<!--客户ID--> b.Name CustomerName ,<!--客户姓名--> b.Mobile CustomerMobile ,<!--客户手机号--> b.CustomerRank ,<!--客储等级--> 0 OpportunityStatus , b.IntentProjectID ProjectID ,<!--意向项目ID--> b.SourceType OpportunitySource ,<!--认知途径ID--> b.CognitiveChannel , b.CognitiveChannelSub , '' SaleUserID , '' SaleUserName , '' TheFirstVisitDate , <!--首访时间--> b.TheLatestFollowUpDate , <!--最后跟进时间--> b.CreateTime ,<!--创建时间--> (CASE WHEN ISNULL(b.Gender,'')='' THEN cp.Gender ELSE b.Gender END) AS Gender ,<!--性别--> b.SourceType , b.ReportUserID , b.ReportUserName , b.ReportUserMobile , b.CreateTime AS ReportTime , <!--报备时间--> b.Status ClueStatus, '' SaleTeamID, CO.OrgName ChannelName, b.ReportUserOrg, '' ZJDF FROM B_Clue b LEFT JOIN B_ChannelOrg CO ON CO.ID=b.ReportUserOrg LEFT JOIN dbo.B_CustomerPotential CP ON b.CustomerPotentialID=CP.ID WHERE EXISTS( SELECT OpportunityID FROM #table tab WHERE tab.OpportunityID=b.ID )
) m ) t

select C.Name CustomerName, STUFF(C.Mobile ,4,4,'****') CustomerMobile, CASE dbo.F_DictName(O.CustomerRank) WHEN '' THEN '1级' WHEN NULL THEN '1级' ELSE dbo.F_DictName(O.CustomerRank) END CustomerRank , dbo.F_GetMediaLargeName(O.CognitiveChannel) CognitiveChannel, dbo.F_GetMediaChildName(O.CognitiveChannelSub) CognitiveChannelSub, dbo.F_DictName(O.OpportunitySource) OpportunitySource, dbo.F_DictName(O.Commercial) Commercial, O.CreateTime, O.TheFirstVisitDate, dbo.F_GetZJDF(O.ID) ZJDF, O.TheLatestFollowUpDate, dbo.F_DictName(O.IsLittleBooking) IsLittleBooking, dbo.F_GetSaleUserName(O.SaleUserID) SaleUserName, dbo.F_DictName(O.CustomerLevel) CustomerLevel, dbo.F_DictName(O.PropertyIntention) PropertyIntention, dbo.F_DictName(A.DomicilePlace) DomicilePlace, dbo.F_DictName(A.HomeArea) HomeArea, dbo.F_DictName(A.WorkArea) WorkArea, dbo.F_DictName(A.AgeGroup) AgeGroup, A.Birthday, A.HomeAddress, dbo.F_DictName(A.PropertyNum) PropertyNum, dbo.F_DictName(A.Marriage) Marriage, dbo.F_DictName(A.Family) Family, dbo.F_DictName(A.Industry) Industry, C.IsOwner, dbo.F_DictName(C.IsEmployee) IsEmployee, dbo.F_DictName(C.CardType) CardType, C.CardID, dbo.F_DictName(C.AcceptFactor) AcceptFactor, dbo.F_DictName(C.Gender) Gender, C.Creator, '' NeedArea, '' IntertionTotal, Cl.ReportUserName, Ch.OrgName,
dbo.F_GetStatusName(O.Status) Status
from B_Customer C left join B_CustomerAttribute A on A.CustomerID=C.ID and A.IsDel=0 left join B_Opportunity O on C.ID=O.CustomerID and C.IsDel=0 left join B_Clue Cl on Cl.ID=O.ClueID AND Cl.ReportUserID=#{ReportUserID} left join B_ChannelOrg Ch on Ch.ID=Cl.ReportUserOrg where O.IsDel=0 AND C.ID=#{CustomerID} AND O.ProjectID=#{ProjectID}
