mapper.select:

SELECT co.ID,co.OrgName FROM dbo.B_ChannelOrg co
  INNER JOIN dbo.B_PojectChannelOrgRel pc ON co.ID=pc.OrgID AND pc.IsDel=0 AND pc.Status=1  AND pc.ProjectID=#{projectID}
  WHERE co.IsDel=0 AND co.Status=1  AND co.OrgCategory=1

SELECT m.ClueID, m.CustomerID, m.CustomerName, m.CustomerMobile,
CASE WHEN dbo.F_DictName(m.CustomerRank) <> '' THEN dbo.F_DictName(m.CustomerRank) ELSE '1级' END CustomerRank, dbo.F_GetClueStatusName(m.Status,0) Status, m.InvalidReason, m.ComeOverdueTime, m.TradeOverdueTime, dbo.F_GetSaleUserName(m.ConfirmUserId) ConfirmUserName, m.ConfirmTime, m.ProjectID, dbo.F_DictName(m.SourceType) SourceType, m.ReportTime, m.TheFirstVisitDate, m.ReportUserID , m.ReportUserName , m.ReportUserMobile , m.ChannelName, m.OpportunityID, dbo.F_GetZJDF(m.OpportunityID) ZJDF FROM ( SELECT * FROM ( SELECT b.ID ClueID , b.CustomerPotentialID CustomerID , b.Name CustomerName , b.Mobile CustomerMobile ,
CASE WHEN o.CustomerRank IS NOT NULL THEN o.CustomerRank ELSE b.CustomerRank END CustomerRank, b.Status, b.InvalidReason, b.ComeOverdueTime, b.TradeOverdueTime, b.ConfirmUserId, b.ConfirmTime, b.IntentProjectID ProjectID , b.SourceType, b.ReportUserID , b.ReportUserName , b.ReportUserMobile , b.CreateTime AS ReportTime , o.TheFirstVisitDate , CO.OrgName ChannelName, o.ID OpportunityID, b.Status Statu FROM B_Clue b LEFT JOIN dbo.B_Opportunity o ON b.ID=o.ClueID AND o.IsDel =0 AND o.ProjectID=#{projectID} LEFT JOIN B_ChannelOrg CO ON CO.ID=b.ReportUserOrg AND CO.IsDel =0 LEFT JOIN dbo.B_CustomerAttach A ON A.OpportunityID = o.ID AND A.IsDel =0 AND A.ProjectID=#{projectID} WHERE b.IsDel = 0 AND b.IntentProjectID = #{projectID}
) t WHERE 1=1 ${sqlWhere} ) m

select C.Name CustomerName, STUFF(C.Mobile, 4, 4, '****') CustomerMobile, dbo.F_DictName(O.CustomerRank) CustomerRank, dbo.F_GetMediaLargeName(Cl.CognitiveChannel) CognitiveChannel, dbo.F_GetMediaChildName(Cl.CognitiveChannelSub) CognitiveChannelSub, dbo.F_DictName(O.OpportunitySource) OpportunitySource, dbo.F_DictName(O.Commercial) Commercial, O.CreateTime, O.TheFirstVisitDate, dbo.F_GetZJDF(O.ID) ZJDF, O.TheLatestFollowUpDate, dbo.F_DictName(O.IsLittleBooking) IsLittleBooking, dbo.F_GetSaleUserName(O.SaleUserID) SaleUserName, dbo.F_DictName(O.CustomerLevel) CustomerLevel, dbo.F_DictName(O.PropertyIntention) PropertyIntention, dbo.F_DictName(A.DomicilePlace) DomicilePlace, dbo.F_DictName(A.HomeArea) HomeArea, dbo.F_DictName(A.WorkArea) WorkArea, dbo.F_DictName(A.AgeGroup) AgeGroup, A.Birthday, A.HomeAddress, dbo.F_DictName(A.PropertyNum) PropertyNum, dbo.F_DictName(A.Marriage) Marriage, dbo.F_DictName(A.Family) Family, dbo.F_DictName(A.Industry) Industry, C.IsOwner, dbo.F_DictName(C.IsEmployee) IsEmployee, dbo.F_DictName(C.CardType) CardType, C.CardID, dbo.F_DictName(C.AcceptFactor) AcceptFactor, dbo.F_DictName(C.Gender) Gender, C.Creator, '' NeedArea, '' IntertionTotal, Cl.ReportUserName, Ch.OrgName,
dbo.F_GetStatusName(O.Status) Status
from B_Customer C left join B_CustomerAttribute A on A.CustomerID = C.ID and A.IsDel = 0 left join B_Opportunity O on C.ID = O.CustomerID and C.IsDel = 0 left join B_Clue Cl on Cl.ID = O.ClueID left join B_ChannelOrg Ch on Ch.ID = Cl.ReportUserOrg where O.IsDel = 0 AND C.ID = #{CustomerID} AND O.ProjectID = #{projectID}


SELECT  Mobile
FROM dbo.B_CustomerPotential
WHERE ID = #{customerID}


{call	 [dbo].[P_SourceTypeChange_Update](#{oldClueID,mode=IN},
                                        #{clueID,mode=IN},
                                        #{userID,mode=IN},
                                        #{reason,mode=IN},
                                        #{enclosure,mode=IN}
)}


{call	 [dbo].[P_ExtendProtectDetail_Update](#{protectNum,mode=IN},
                                        #{clueID,mode=IN},
                                        #{userID,mode=IN},
                                        #{reason,mode=IN},
                                        #{enclosure,mode=IN}
)}


{call	 [dbo].[P_SetInvalidDetail_Update](
                                        #{clueID,mode=IN},
                                        #{userID,mode=IN},
                                        #{reason,mode=IN},
                                        #{enclosure,mode=IN}
)}


{call	 [dbo].[P_ChangeSourceTypeDetail_Update](
                                        #{clueID,mode=IN},
                                        #{SourceType,mode=IN},
                                        #{reason,mode=IN},
                                        #{enclosure,mode=IN},
                                        #{userID,mode=IN},
                                        #{type,mode=IN}
)}


{call	 [dbo].[P_ChangeCognitiveChannelDetail_Update](
                                        #{clueID,mode=IN},
                                        #{cognitiveChannel,mode=IN},
                                        #{reason,mode=IN},
                                        #{enclosure,mode=IN},
                                        #{userID,mode=IN},
                                        #{type,mode=IN}
)}


select ID
from dbo.B_Opportunity
WHERE ID = #{opportunityID}

select C.Name CustomerName, STUFF(C.Mobile, 4, 4, '****') CustomerMobile, CASE dbo.F_DictName(O.CustomerRank) WHEN '' THEN '1级' WHEN NULL THEN '1级' ELSE dbo.F_DictName(O.CustomerRank) END CustomerRank, dbo.F_GetMediaLargeName(O.CognitiveChannel) CognitiveChannel, dbo.F_GetMediaChildName(O.CognitiveChannelSub) CognitiveChannelSub, dbo.F_DictName(O.OpportunitySource) OpportunitySource, dbo.F_DictName(O.Commercial) Commercial, O.CreateTime, O.TheFirstVisitDate, dbo.F_GetZJDF(O.ID) ZJDF, O.TheLatestFollowUpDate, dbo.F_DictName(O.IsLittleBooking) IsLittleBooking, dbo.F_GetSaleUserName(O.SaleUserID) SaleUserName, dbo.F_DictName(O.CustomerLevel) CustomerLevel, dbo.F_DictName(O.PropertyIntention) PropertyIntention, dbo.F_DictName(A.DomicilePlace) DomicilePlace, dbo.F_DictName(A.HomeArea) HomeArea, dbo.F_DictName(A.WorkArea) WorkArea, dbo.F_DictName(A.AgeGroup) AgeGroup, A.Birthday, A.HomeAddress, dbo.F_DictName(A.PropertyNum) PropertyNum, dbo.F_DictName(A.Marriage) Marriage, dbo.F_DictName(A.Family) Family, dbo.F_DictName(A.Industry) Industry, C.IsOwner, dbo.F_DictName(C.IsEmployee) IsEmployee, dbo.F_DictName(C.CardType) CardType, C.CardID, dbo.F_DictName(C.AcceptFactor) AcceptFactor, dbo.F_DictName(C.Gender) Gender, C.Creator, '' NeedArea, '' IntertionTotal, Cl.ReportUserName, Ch.OrgName,
dbo.F_GetStatusName(O.Status) Status
from B_Customer C left join B_CustomerAttribute A on A.CustomerID = C.ID and A.IsDel = 0 left join B_Opportunity O on C.ID = O.CustomerID and C.IsDel = 0 left join B_Clue Cl on Cl.ID = O.ClueID AND Cl.ReportUserID = #{reportUserID} left join B_ChannelOrg Ch on Ch.ID = Cl.ReportUserOrg where O.IsDel = 0 AND C.ID = #{customerID} AND O.ProjectID = #{projectID}
