SELECT tr.OpportunityID , tr.CustomerID , tr.CustomerName , STUFF(tr.CustomerMobile ,4,4,'****') CustomerMobile , tr.CustomerMobile CustomerMobileWhole , CASE dbo.F_DictName(tr.CustomerRank) WHEN '' THEN '1级' WHEN NULL THEN '1级' ELSE dbo.F_DictName(tr.CustomerRank) END CustomerRankName , tr.ClueStatus clueStatus, tr.OpportunityStatus OpportunityStatus, dbo.F_GetClueStatusName_xkc(tr.ClueStatus, tr.OpportunityStatus) CustomerStatus , dbo.F_GetProjectName(tr.ProjectID) AS IntentProjectName , dbo.F_DictName(tr.OpportunitySource) OpportunitySource , dbo.F_GetMediaLargeName(tr.CognitiveChannel) CognitiveChannel , dbo.F_GetMediaChildName(tr.CognitiveChannelSub) CognitiveChannelSub , tr.SaleUserID , tr.SaleUserName , tr.TheFirstVisitDate , tr.ZJDF, tr.TheLatestFollowUpDate , tr.CreateTime , tr.Gender , dbo.F_DictName(tr.SourceType) AS SourceType , tr.ReportUserID , tr.ReportUserName , tr.ReportUserMobile , tr.ChannelId , tr.ChannelName , tr.ReportTime , dbo.F_DictName(tr.Gender) GenderTxt, dbo.F_GetSaleGroupName(tr.SaleUserID, tr.ProjectID) AS SaleTeamName, STUFF( ( SELECT ',' + r.RoomCode FROM dbo.B_Room r INNER JOIN dbo.B_Building b ON r.BuildingID=b.ID WHERE r.ID IN (SELECT mt.RoomGUID FROM dbo.B_Opportunity o INNER JOIN dbo.B_CustomerAttach ca ON o.ID=ca.OpportunityID INNER JOIN dbo.C_MYTrade mt ON mt.OppGUID=ca.ID WHERE o.ID=tr.OpportunityID) FOR XML PATH('') ) , 1, 1, '') Room, tr.SourceType SourceTypeID, tr.CardID, tr.CardType, tr.SpareMobile, tr.HomeAddress, tr.Address Address, tr.attrId, tr.cityName, tr.areaName FROM ( SELECT b.ID OpportunityID , b.CustomerPotentialID CustomerID , b.Name CustomerName , b.Mobile CustomerMobile , b.CustomerRank , 0 OpportunityStatus , b.IntentProjectID ProjectID , b.SourceType OpportunitySource , b.CognitiveChannel , b.CognitiveChannelSub , '' SaleUserID , '' SaleUserName , '' TheFirstVisitDate , b.TheLatestFollowUpDate , b.CreateTime , (CASE WHEN ISNULL(b.Gender,'')='' THEN cp.Gender ELSE b.Gender END) AS Gender , b.SourceType , b.ReportUserID , b.ReportUserName , b.ReportUserMobile , b.CreateTime AS ReportTime , b.Status ClueStatus, '' SaleTeamID, CO.ID ChannelId, CO.OrgName ChannelName, b.ReportUserOrg, '' ZJDF, '' CardID, '' CardType, '' SpareMobile, '' HomeAddress, '' Address, '' attrId, orgCity.OrgName cityName, orgArea.OrgName areaName FROM B_Clue b LEFT JOIN B_ChannelOrg CO ON CO.ID=b.ReportUserOrg LEFT JOIN dbo.B_CustomerPotential CP ON b.CustomerPotentialID=CP.ID LEFT JOIN B_Project pro ON pro.ID = b.IntentProjectID LEFT JOIN S_Organization orgCity ON orgCity.ID = pro.BUGUID LEFT JOIN S_Organization orgArea ON orgArea.id = orgCity.PID WHERE EXISTS(SELECT OpportunityID FROM (select OpportunityID from ( SELECT * FROM ( SELECT b.ID OpportunityID , b.CreateTime , 0 OpportunityStatus, '' TheFirstVisitDate, b.Status ClueStatus, '' ZJDF, '' TheLatestFollowUpDate FROM B_Clue b LEFT JOIN B_ChannelOrg CO ON CO.ID=b.ReportUserOrg LEFT JOIN dbo.B_CustomerPotential CP ON b.CustomerPotentialID=CP.ID WHERE b.IsDel = 0
--AND b.SourceType IS NOT NULL AND b.SourceType != '0390CD8C-D6D4-4C92-995B-08C7E18E6EC2' --AND NOT EXISTS ( SELECT ID -- FROM B_Opportunity d -- WHERE d.IsDel = 0 -- AND d.ClueID = b.ID ) -- AND b.Status!=3 AND b.CustomerPotentialID = #{id} ) tm ) ts ) tab WHERE tab.OpportunityID=b.ID )
) tr
