mapper.select:
       SELECT
			*
		FROM
			B_RiskInfo
		WHERE
            RiskType not in (4,6)

            and RegionalId = #{regionalId}


            and CityId = #{cityId}


            and ProjectId = #{projectId}


            and CreateTime between  #{startTime} and  #{endTime}

SELECT
*
FROM
B_RiskInfo
WHERE
RiskType = 6

    and RegionalId = #{regionalId}


    and CityId = #{cityId}


    and ProjectId = #{projectId}


    and CreateTime between  #{startTime} and  #{endTime}

SELECT
*
FROM
B_RiskInfo
WHERE


        RiskType = #{riskType}


        RiskType != 6




    and CustomerName like %#{userName}%


    and SaleUserName like %#{saleUserName}%



    and AdviserGroupID = #{adviserGroupID}



    and CustomerMobile = #{customerMobile}



    and ReportUserName like %#{reportUserName}%



    and CustomerStatus = #{customerStatus}



    and CreateTime between  #{startTime} and  #{endTime}



    and RegionalId = #{regionalId}



    and CityId = #{cityId}



    and ProjectId = #{projectId}

SELECT
*
FROM
B_RiskInfo
WHERE
RiskType != 6

    and RegionalId = #{regionalId}


