mapper.select:
SELECT TOP ${pageSize} RegionalId, CityId, ProjectId, ChannelCompany FROM ( SELECT UPPER (RegionalId) RegionalId, UPPER (CityId) CityId, UPPER (ProjectId) ProjectId, UPPER (ChannelCompany) ChannelCompany, row_number () OVER ( ORDER BY RegionalId, CityId, ProjectId, ChannelCompany ASC ) AS rownumber FROM B_WxbRiskCount WHERE 1 = 1
AND RegionalId = #{ regionalId }
AND CityId = #{ cityId }
AND ProjectId = #{ projectId }
AND DictId = #{ channelSource }
AND ChannelCompany LIKE CONCAT('%',#{ channelOrg },'%')
AND FreshCardTime BETWEEN #{ startTime } AND #{ endTime }
GROUP BY RegionalId, CityId, ProjectId, ChannelCompany ) temp_row WHERE rownumber > ((#{pageNum} - 1) * #{pageSize})

SELECT COUNT(a.c) FROM ( SELECT COUNT(*) c FROM B_WxbRiskCount WHERE 1=1
AND RegionalId = #{ regionalId }
AND CityId = #{ cityId }
AND ProjectId = #{ projectId }
AND DictId = #{ channelSource }
AND ChannelCompany LIKE CONCAT('%',#{ channelOrg },'%')
AND FreshCardTime BETWEEN #{ startTime } AND #{ endTime }
GROUP BY RegionalId, CityId, ProjectId, ChannelCompany ) a
