mapper.select:
SELECT TOP ${pageSize} RegionalId, CityId, ProjectId, DictId, DictName, ChannelCompanyId, ChannelCompany FROM ( SELECT UPPER (RegionalId) RegionalId, UPPER (CityId) CityId, UPPER (ProjectId) ProjectId, UPPER (DictId) DictId, DictName, ChannelCompanyId, ChannelCompany, row_number () OVER ( ORDER BY RegionalId, CityId, ProjectId, ChannelCompany ASC ) AS rownumber FROM B_WxbRiskCount WHERE 1 = 1
AND RegionalId = #{ regionalId }
AND CityId = #{ cityId }
AND ProjectId = #{ projectId }
AND DictId IN
#{item}
AND ChannelCompanyId = #{ channelCompanyId }
AND FreshCardTime BETWEEN #{ startTime } AND #{ endTime }
GROUP BY RegionalId, CityId, ProjectId, ChannelCompany ) temp_row WHERE rownumber > ((#{pageNum} - 1) * #{pageSize})

SELECT
    COUNT(a.c)
FROM
    (
        SELECT
            COUNT(*) c
        FROM
            B_WxbRiskCount
        WHERE
            1=1

                AND RegionalId = #{ regionalId }


                AND CityId = #{ cityId }


                AND ProjectId = #{ projectId }


                AND DictId IN

                    #{item}



                AND ChannelCompanyId = #{ channelCompanyId }


                AND FreshCardTime BETWEEN #{ startTime } AND #{ endTime }

        GROUP BY
            RegionalId,
            CityId,
            ProjectId,
            ChannelCompany
    ) a
