mapper.select:

    SELECT * FROM (
		SELECT ROW_NUMBER() OVER ( ORDER BY CreateTime DESC ) num, *
		FROM   (
				SELECT BizType, BizDesc, IP, Ext1, Ext2, Ext3, Data, CreateTime
				FROM dbo.S_Logs_Bak 
				WHERE 1=1 ${str1}
				UNION
				SELECT BizType, BizDesc, IP, Ext1, Ext2, Ext3, Data, CreateTime
				FROM dbo.S_Logs 
				WHERE 1=1 ${str1} 
				 )sp
		)t 
		WHERE t.num > ( #{PageIndex} - 1 ) * #{PageSize}
		        AND t.num <= #{PageIndex} * #{PageSize};


    SELECT  COUNT(1) recordCount 
		FROM   (
				SELECT BizType, BizDesc, IP, Ext1, Ext2, Ext3, Data, CreateTime
				FROM dbo.S_Logs_Bak 
				WHERE 1=1 ${str1}
				UNION
				SELECT BizType, BizDesc, IP, Ext1, Ext2, Ext3, Data, CreateTime
				FROM dbo.S_Logs 
				WHERE 1=1 ${str1} 
				 )a


    SELECT * FROM (
		SELECT ROW_NUMBER() OVER ( ORDER BY CreateTime DESC ) num, *
		FROM   (
				SELECT BizType, BizDesc, IP, Ext1, Ext2, Ext3, Data, CreateTime
				FROM dbo.S_Logs_Bak 
				WHERE 1=1 ${str1}
				UNION
				SELECT BizType, BizDesc, IP, Ext1, Ext2, Ext3, Data, CreateTime
				FROM dbo.S_Logs 
				WHERE 1=1 ${str1} 
